import Avatar from '../src/Avatar.vue'
import { mount } from '@vue/test-utils'
import { nextTick } from 'vue'
import {
  expectHaveClass,
  expectHaveAttribute,
  expectHaveStyle,
  expectToBeExist,
  expectNotToBeExist,
  expectHaveTextContent
} from '../../../../tests/helper'

export const IMAGE_SUCCESS =
  'data:image/png;base64,'

export const IMAGE_FAIL = 'data:image/png;base64,fail'

describe('Avatar.props', () => {
  it('snapshot', () => {
    const wrapper = mount(Avatar)
    expect(wrapper.element).toMatchSnapshot()
  })

  describe('icon', () => {
    it('should render props.icon', () => {
      const wrapper = mount(Avatar, {
        props: {
          icon: 'el-icon-eleme'
        }
      })

      expectHaveClass(wrapper.get('i'), 'el-icon-eleme')
    })
  })

  describe('size', () => {
    it('size is number', () => {
      const wrapper = mount(Avatar, {
        props: {
          size: 100
        }
      })

      expectHaveStyle(wrapper.get('span'), {
        width: '100px',
        height: '100px',
        lineHeight: '100px'
      })
    })

    it('size is string', () => {
      const wrapper = mount(Avatar, {
        props: {
          size: 'large'
        }
      })

      expectToBeExist(wrapper.find('.el-avatar--large'))
    })
  })
  describe('shape', () => {
    it('should render shape by props.shape', () => {
      const wrapper = mount(Avatar, {
        props: {
          shape: 'circle'
        }
      })
      expectToBeExist(wrapper.find('.el-avatar--circle'))
    })
  })

  describe('src', () => {
    it('should render image props.src', () => {
      const wrapper = mount(Avatar, {
        props: {
          src: IMAGE_SUCCESS
        }
      })
      expectHaveAttribute(wrapper.get('img'), 'src', IMAGE_SUCCESS)
    })
  })
  describe('altSet', () => {
    it('should render  props.srcSet', () => {
      const wrapper = mount(Avatar, {
        props: {
          src: IMAGE_SUCCESS,
          alt: 'girl',
          srcSet: 'big.jpg 1440w,middle.jpg 800w'
        }
      })

      expectHaveAttribute(
        wrapper.get('img'),
        'srcset',
        'big.jpg 1440w,middle.jpg 800w'
      )
    })
  })
  describe('alt', () => {
    it('should render  props.alt', () => {
      const wrapper = mount(Avatar, {
        props: {
          src: IMAGE_SUCCESS,
          alt: 'girl'
        }
      })
      expectHaveAttribute(wrapper.get('img'), 'alt', 'girl')
    })
  })

  describe('fit', () => {
    it('should render  props.fit', () => {
      const wrapper = mount(Avatar, {
        props: {
          src: IMAGE_SUCCESS,
          fit: 'contain'
        }
      })

      expectHaveStyle(wrapper.get('img'), {
        objectFit: 'contain'
      })
    })
  })

  describe('error', () => {
    it('image load faild should call customer callback', async () => {
      const callback = jest.fn(() => false)
      const wrapper = await mount(Avatar, {
        props: {
          src: IMAGE_SUCCESS,
          error: callback
        }
      })

      wrapper.find('img').trigger('error')
      await nextTick()
      expectToBeExist(wrapper.find('img'))

      expect(callback).toHaveBeenCalled()
    })

    it('image load faild should call customer callback and hidden image', async () => {
      const callback = jest.fn(() => true)
      const wrapper = await mount(Avatar, {
        props: {
          src: IMAGE_SUCCESS,
          error: callback
        }
      })

      wrapper.find('img').trigger('error')
      await nextTick()
      expectNotToBeExist(wrapper.find('img'))
      expect(callback).toHaveBeenCalled()
    })
  })
  describe('solt', () => {
    it('should render  props.solt', () => {
      const wrapper = mount(Avatar, {
        slots: {
          default: () => {
            return '<span>default slot</span>'
          }
        }
      })

      expectHaveTextContent(wrapper.find('span'), '<span>default slot</span>')
    })
  })
})
